#!/bin/bash

# See also https://github.com/mathiasbynens/dotfiles/blob/master/.osx

if [ -z "$OS" ]; then
  . ../lib/common.sh
fi;

if [ "$OS" != "macos" ]; then exit; fi

# Add theme "MyPro" (based on Pro, font-size 14, close if no error)
defaults write com.apple.Terminal "Window Settings" -dict-add MyPro '<dict><key>BackgroundBlur</key><real>0.0</real><key>BackgroundColor</key><data></data><key>CursorColor</key><data></data><key>Font</key><data></data><key>FontAntialias</key><false/><key>FontWidthSpacing</key><real>0.99596774193548387</real><key>ProfileCurrentVersion</key><real>2.04</real><key>SelectionColor</key><data></data><key>ShowWindowSettingsNameInTitle</key><false/><key>TextBoldColor</key><data></data><key>TextColor</key><data></data><key>name</key><string>MyPro</string><key>shellExitAction</key><integer>1</integer><key>type</key><string>Window Settings</string></dict>'

# Use theme "MyPro"
defaults write com.apple.terminal "Default Window Settings" -string MyPro
defaults write com.apple.terminal "Startup Window Settings" -string MyPro
